CREATE MEMORY TABLE BOOKS(ISBN VARCHAR(255) NOT NULL PRIMARY KEY,NAME VARCHAR(255))
CREATE MEMORY TABLE USERS(ID INTEGER GENERATED BY DEFAULT AS IDENTITY(START WITH 0) NOT NULL PRIMARY KEY,USERNAME VARCHAR(255),PASSWORD VARCHAR(255))
CREATE MEMORY TABLE RENTS(ID INTEGER GENERATED BY DEFAULT AS IDENTITY(START WITH 0) NOT NULL PRIMARY KEY,BOOKISBN VARCHAR(255),USERID INTEGER,RENTDATE DATE,CONSTRAINT SYS_FK_50 FOREIGN KEY(BOOKISBN) REFERENCES BOOKS(ISBN),CONSTRAINT SYS_FK_51 FOREIGN KEY(USERID) REFERENCES USERS(ID))